    function comprobarCorreo($Correo){
        $resultado = 0;
        $sentencia = "SELECT COUNT(*) AS `cantidad` FROM `usuarios` WHERE `correoUsuario` LIKE '$Correo'";
        $ejecutar = mysqli_query($GLOBALS["db"], $sentencia);
        while ($fila = mysqli_fetch_array($ejecutar)) {
            $resultado = $fila['cantidad'];
        }

        if ($resultado != 0){ ?>
            <small>Por favor, prueba a iniciar sesi√≥n: <span class="text-danger font-weight-700">Este correo ya esta en uso</span></small>
        <?php }
        return $resultado;
    }

    #INICIO
